package std.demo.local.httpClient;

import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

import org.apache.http.HttpEntity;
import org.apache.http.StatusLine;
import org.apache.http.client.ClientProtocolException;
import org.apache.http.client.config.RequestConfig;
import org.apache.http.client.methods.CloseableHttpResponse;
import org.apache.http.client.methods.HttpGet;
import org.apache.http.client.methods.HttpPost;
import org.apache.http.client.methods.HttpRequestBase;
import org.apache.http.entity.StringEntity;
import org.apache.http.impl.client.CloseableHttpClient;
import org.apache.http.impl.client.HttpClients;
import org.apache.http.util.EntityUtils;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import com.fasterxml.jackson.databind.ObjectMapper;

import std.demo.AESUtil;

/**
 * http请求
 * 
 * @author zy
 *
 */
public class HttpUtil {
	private static final Logger log = LoggerFactory.getLogger(HttpUtil.class);

	/**
	 * http请求类型
	 * 
	 * @author zy
	 *
	 */
	private enum HttpMethod {
		GET, POST
	}

	/**
	 * http响应实体
	 * 
	 * @author zy
	 *
	 */
	public static class HttpRep {
		private Integer statusCode;
		private String content;

		public Integer getStatusCode() {
			return statusCode;
		}

		public void setStatusCode(Integer statusCode) {
			this.statusCode = statusCode;
		}

		public String getContent() {
			return content;
		}

		public void setContent(String content) {
			this.content = content;
		}

		@Override
		public String toString() {
			return "HttpRep [statusCode=" + statusCode + ", content=" + content + "]";
		}
	}

	/**
	 * 执行post请求
	 * 
	 * @param url
	 * @return
	 */
	public static HttpRep doPost(String url) {
		return doPost(null, url);
	}

	/**
	 * 执行post请求
	 * 
	 * @param messageBody
	 * @param url
	 * @return
	 */
	public static HttpRep doPost(String messageBody, String url) {
		return doHttp(HttpMethod.POST, url, messageBody);
	}

	/**
	 * 执行get请求
	 * 
	 * @param url
	 * @return
	 */
	public static HttpRep doGet(String url) {
		return doHttp(HttpMethod.GET, url, null);
	}

	private static HttpRep doHttp(HttpMethod method, String url, String messageBody) {
		HttpRep rep = new HttpRep();
		CloseableHttpClient httpClient = null;
		CloseableHttpResponse httpResponse = null;
		HttpRequestBase request = null;
		try {
			httpClient = HttpClients.createDefault();
			if (method == HttpMethod.POST) {
				request = new HttpPost(url);

				if (messageBody != null && !"".equals(messageBody.trim())) {
					StringEntity stringEntity = new StringEntity(messageBody, "UTF-8");
					((HttpPost) request).setEntity(stringEntity);
				}
			} else if (method == HttpMethod.GET) {
				request = new HttpGet(url);
			} else {
				throw new IllegalArgumentException("method not supported");
			}

			RequestConfig requestConfig = RequestConfig.custom().setSocketTimeout(5000).setConnectTimeout(5000).build();
			// 设置请求和传输超时时间
			request.setConfig(requestConfig);

			httpResponse = httpClient.execute(request);

			// 获取响应码
			StatusLine sl = httpResponse.getStatusLine();

			int statusCode = sl.getStatusCode();

			rep.setStatusCode(statusCode);

			HttpEntity entity = httpResponse.getEntity();

			if (entity != null) {
				
				System.out.println(entity.getClass());
				
				rep.setContent(EntityUtils.toString(entity, "utf-8"));
			}
			return rep;
		} catch (Exception e) {
			log.error("http请求发送失败", e);
			return null;
		} finally {
			try {
				if (httpResponse != null)
					httpResponse.close();
				if (httpClient != null)
					httpClient.close();
			} catch (IOException e) {
				e.printStackTrace();
			}
		}
	}

	public static void main(String[] args) throws ClientProtocolException, IOException {
		
		
		
		System.out.println(AESUtil.decrypt("", "make it happened"));
		
		Map<String,String> params = new HashMap<String, String>();
		
		params.put("appId", "app_gkt_ringtones_meowers_3487");
		params.put("method", "content.audio.list");
		params.put("timestamp", "1500432993304"); 
		params.put("format", "json"); 
		params.put("v", "1.0"); 
		params.put("sign", "84AEA7DA5231ED1FD9EEC1D872ECF408"); 
		params.put("appSecret", "6d78471eacf9"); 
		params.put("categoryId", "003"); 
		params.put("pageNo", "1"); 
		params.put("pageSize", "20"); 
		params.put("language", "TH"); 

		
		
		
		ObjectMapper objectMapper = new ObjectMapper();
		
		
		
		String encryptStr = AESUtil.encrypt(objectMapper.writeValueAsString(params), "make it happened");
		
		
		String result = doPost(encryptStr, "http://172.30.120.57:8080/content/app/dispatch/encrypt").getContent();
		
		
		
		System.out.println(result);
		System.out.println(AESUtil.decrypt(result, "make it happened"));
		
	}
}
